mapper.update:
update merchant
set mLock = #{mLock}
where id = #{mId}

mapper.select:
select id,userName,tell,mType,mLock,state,shopName,idName,idCard
from merchant

  state=1

   and id=#{id}


   and userName=#{username}


    and tell=#{tell}


    and mType=#{mType}


    and mLock=#{mLock}


    and shopName=#{shopName}

select *
from merchant

  state=0 and mLock=0

    and id=#{id}


    and userName=#{username}


    and tell=#{tell}


    and mType=#{mType}


    and shopName=#{shopName}

mapper.update:
update merchant
set state = #{state}
where id = #{mId}
